mapper.select:
select tmp.id , tmp.status , tmp.reason , tmp.canceled_at , tmp.approved_at , tmp.created_at , tmp.updated_at , buyer.id buyer_id , buyer.username buyer_username , buyer.name buyer_name , buyer.phone buyer_phone from tbl_market_purchase tmp inner join tbl_user buyer on buyer.id = tmp.buyer_id inner join tbl_market tm on tmp.market_id = tm.id inner join tbl_user seller on seller.id = tm.seller_id
and buyer.id = #{buyer_id}
and seller.id = #{seller_id}
and tm.id = #{market_id}
and tmp.id = #{purchase_id}
and tmp.status = #{purchase_status}
and tm.status = #{market_status}

select tmp.id , tmp.status , tmp.reason , tmp.canceled_at , tmp.approved_at , tmp.created_at , tmp.updated_at , buyer.id buyer_id , buyer.username buyer_username , buyer.name buyer_name , buyer.phone buyer_phone from tbl_market_purchase tmp inner join tbl_user buyer on buyer.id = tmp.buyer_id inner join tbl_market tm on tmp.market_id = tm.id inner join tbl_user seller on seller.id = tm.seller_id
and buyer.id = #{buyer_id}
and seller.id = #{seller_id}
and tm.id = #{market_id}
and tmp.id = #{purchase_id}
and tmp.status = #{purchase_status}
and tm.status = #{market_status}
order by tmp.created_at desc
limit #{paging.limit} offset #{paging.offset}

select
    count(1)
from tbl_market_purchase tmp
inner join tbl_user buyer on buyer.id = tmp.buyer_id
inner join tbl_market tm on tmp.market_id = tm.id
inner join tbl_user seller on seller.id = tm.seller_id


        and buyer.id = #{buyer_id}


        and seller.id = #{seller_id}


        and tm.id = #{market_id}


        and tmp.id = #{purchase_id}


        and tmp.status = #{purchase_status}


        and tm.status = #{market_status}

select purchase_id , `from`.id from_id , `from`.username from_username , `from`.name from_name , `from`.phone from_phone , `to`.id to_id , `to`.username to_username , `to`.name to_name , `to`.phone to_phone , tmpr.reason , tmpr.created_at , tmpr.updated_at from tbl_market_purchase_report tmpr inner join tbl_user `from` on `from`.id = tmpr.`from` inner join tbl_user `to` on `to`.id = tmpr.`to`
and tmpr.purchase_id = #{purchase_id}
and `from`.id = #{from}
and `to`.id = #{to}

mapper.update:
update tbl_market_purchase


        reason = #{reason}


        , status = #{status}


        , canceled_at = #{canceled_at}


        , approved_at = #{approved_at}




        and id = #{purchase_id}
